import React from 'react';
import InputFilter from './InputFilter';


const SelectionFilter = ({name, options}) => {
    return (
        <div className="selection">
            <select name={name} id={name}>
                {
                    options.map((option)=>{
                        return <option value={option}></option>
                    })
                }
            </select>
        </div>
    )
}

const Filters = ({setMinPriceHandler, setMaxPriceHandler, minPrice, maxPrice, estateOptions}) => {
    return (
        <form action="">
            <SelectionFilter name="estate" options={estateOptions}/>
            <InputFilter label="Price(min): " name="minPrice" value={minPrice} onChangeHandler={setMinPriceHandler}/> 
            <InputFilter label="Price(max): " name="maxPrice" value={maxPrice} onChangeHandler={setMaxPriceHandler}/> 
        </form>
    )
}

export default Filters;
