import React from 'react';
import InputFilter from './InputFilter';


const SelectionFilter = ({name, options, onChangeHandler, value, enable}) => {
    return (
        <div className="selection">

            <label htmlFor={name}>Use {name} filter: </label>
            <input type="checkbox" id={name} name={name} value={!enable}/>

            <select disabled={!enable} name={name} id={name} value={value} onChange={e=>onChangeHandler(e)}>
                {
                    options.map((option)=>{
                        return <option value={option}>{option}</option>
                    })
                }
            </select>
        </div>
    )
}

const Filters = ({setMinPriceHandler, setMaxPriceHandler, minPrice, maxPrice, useEstate, estateValue, estateOptions, setEstateHandler}) => {
    return (
        <form action="">
            <SelectionFilter enable={useEstate} value={estateValue} name="estate" options={estateOptions} onChangeHandler={setEstateHandler}/>
            <InputFilter label="Price(min): " name="minPrice" value={minPrice} onChangeHandler={setMinPriceHandler}/> 
            <InputFilter label="Price(max): " name="maxPrice" value={maxPrice} onChangeHandler={setMaxPriceHandler}/> 
        </form>
    )
}

export default Filters;
