import React, {Fragment} from 'react';

const Selector = ({name, options, filterHandler}) => {
    return (
        <Fragment>
            <label htmlFor={name}>{name}</label>
            <select name={name} id={name}>
                {options.map(opt=>{
                    return <option value="opt">{opt}</option>
                })}
            </select>
        </Fragment>
    ); 
}

const Filters = ({minPriceHandler, maxPriceHandler, minPrice, maxPrice}) => {
    return (
        <form action="">

            <label htmlFor="minPrice" id="minPrice" name="minPrice">Price(min):</label>
            <input type="text" id="minPrice" name="minPrice"
            value={minPrice}
            onChange={minPriceHandler}
            />

            <label htmlFor="maxPrice" id="maxPrice" name="maxPrice">Price(max):</label>
            <input type="text" id="maxPrice" name="maxPrice"
            value={maxPrice}
            onChange={maxPriceHandler}
            />

        </form>
    )
}

export default Filters;
