import React from 'react';
import InputFilter from './InputFilter';


const SelectionFilter = ({name, options, onChangeHandler, value, enable, enableHandler}) => {
    return (
        <div className="selection">

            <label htmlFor={name}>Use {name} filter: </label>
            <input type="checkbox" id={name} name={name} value={!enable} onChange={e=>enableHandler(e)}/>

            <select disabled={!enable} name={name} id={name} value={value} onChange={e=>onChangeHandler(e)}>
                {
                    options.map((option)=>{
                        return <option value={option}>{option}</option>
                    })
                }
            </select>
        </div>
    )
}

const Filters = (
    {setMinPriceHandler, setMaxPriceHandler, minPrice, maxPrice, 
     setMinFloorHandler, setMaxFloorHandler, minFloor, maxFloor,
     enableEstateFilter, estateValue, estateOptions, setEnableEstateFilterHandler, setEstateHandler,
     enableSpecFilter, specValue, specOptions, setSpecHandler, setEnableSpecFilterHandler,
     minUsableArea, maxUsableArea, setMinUsableAreaHandler, setMaxUsableAreaHandler
    }
    ) => {
    return (
        <form action="">
            <SelectionFilter 
            enable={enableEstateFilter} enableHandler={setEnableEstateFilterHandler} value={estateValue} 
            name="estate" options={estateOptions} onChangeHandler={setEstateHandler}/>

            <SelectionFilter 
            enable={enableSpecFilter} enableHandler={setEnableSpecFilterHandler} value={specValue} 
            name="spec" options={specOptions} onChangeHandler={setSpecHandler}/>

            <div>
                <InputFilter label="Floor(min): " name="minFloor" value={minFloor} onChangeHandler={setMinFloorHandler} />
                <InputFilter label="Floor(max): " name="maxFloor" value={maxFloor} onChangeHandler={setMaxFloorHandler} />
            </div>

            <div>
                <InputFilter label="Price(min): " name="minPrice" value={minPrice} onChangeHandler={setMinPriceHandler}/> 
                <InputFilter label="Price(max): " name="maxPrice" value={maxPrice} onChangeHandler={setMaxPriceHandler}/> 
            </div>

            <div>
                <InputFilter label="Usable Area(min): " name="minUsableArea" value={minUsableArea} onChangeHandler={setMinUsableAreaHandler}/> 
                <InputFilter label="Usable Area(max): " name="maxUsableArea" value={maxUsableArea} onChangeHandler={setMaxUsableAreaHandler}/> 
            </div>
        </form>
    )
}

export default Filters;
