import React from 'react';

const DataCell = ({cellData}) => {
    return (
        <td>{cellData}</td>
    );
}

const DataRow = ({rowData}) => {
    return (
        <tr>
            {Object.keys(rowData).map(key=>{
                return <DataCell key={key} cellData ={rowData[key]}/>
            })}
        </tr>
    )
}

const DataTable = ({data, minPrice, maxPrice}) => {
    /* destructure table headers */
    const headers = Object.keys(data[0]);

    let bodyData = [];

    let priceCondition = (Price) => (Number(Price) >= minPrice) && (maxPrice ? Number(Price) <= maxPrice : 999999999);

    let filteredData = data.filter(({Price})=>{
        return priceCondition(Price);
    })

    filteredData.map((obj, index)=>{
        return bodyData.push(<DataRow key={index} rowData={obj}/>)
    })

    return (

        <table>
            <thead>
                <tr>
                    {headers.map(header=>{
                        return <th key={header}>{header}</th>
                    })}
                </tr>
            </thead>

            <tbody>
                {bodyData}
            </tbody>

        </table>
    );
}

export default DataTable;
