import React, {useEffect, useReducer, useState} from 'react';
import XLSX from 'xlsx';
import DataTable from './components/DataTable';
import Filters from './components/Filters';

function reducer(state, action) {
  const {type, payload} = action;

  switch(type) {
    case 'UPDATE_DATA':
      return {...state, data: payload};
    case 'SET_LOADING':
      return {...state, loaded: payload};
    case 'SET_FAIL':
      return {...state, fail: payload};
    case 'SET_MINPRICE':
      return {...state, minPrice: payload};
    case 'SET_MAXPRICE':
      return {...state, maxPrice: payload};
    default:
      return state;
  }
}

function App() { 

  const [dataReady, setDataReady] = useState(false);

  const initialState = {
    data: [],
    loaded: false,
    fail: false,
    minPrice: 0,
    maxPrice: ''
  }
  const [state, dispatch] = useReducer(reducer, initialState);
  const {data, loaded, fail, minPrice, maxPrice} = state;

  /**
   * triggered when user uploads a file;
   * mainly for updating file if need be
   */ 
  const readFile = (file) => {
    const promise = new Promise((res, rej)=>{
      /* read file */
      const fileReader = new FileReader();
      fileReader.readAsArrayBuffer(file);

      fileReader.onload = (e) => {
        const arrayBuffer = e.target.result;
        /* now can read workbook */ 
        const workbook = XLSX.read(arrayBuffer, {type:'buffer'});
        const worksheetName = workbook.SheetNames[0];
        const workSheet = workbook.Sheets[worksheetName];
        const data = XLSX.utils.sheet_to_json(workSheet);
        res(data);
      }

      fileReader.onerror = (err) => rej(err);
    })

    promise.then((d)=>{
      // setData(d[0]);
      // return setLoaded(true);
      dispatch({type:'UPDATE_DATA', payload: d[0]});
      dispatch({type:'SET_LOADING', payload: true});
      return
    })
  }

  /* fetching data and update data state on mount */ 
  useEffect(()=>{
    !dataReady &&
      /* fetch data from ./public/data.json */
      fetch('data.json', {
        headers:{
          "Content-Type": "application/json"
        }
      })
      .then(response=>{
        return response.json();
      })
      .then(data=>{
        // setData(data[0]);
        // return setLoaded(false);
        dispatch({type:'UPDATE_DATA', payload: data[0]});
        dispatch({type:'SET_LOADING', payload: false});
        return 
      })
      .then(()=>{
        return setDataReady(true);
      })
      .catch(err=>{
        // setLoaded(true);
        // setFail(true);
        dispatch({type:'SET_LOADING', payload: true});
        dispatch({type:'SET_FAIL', payload: true});
        throw err;
      })



  }, [dataReady])


  const handleSetMinPrice = (e) => {
    // return setMinPrice(e.target.value);
    dispatch({type:'SET_MINPRICE', payload: e.target.value});
  }
  const handleSetMaxPrice = (e) => {
    // return setMaxPrice(e.target.value);
    dispatch({type:'SET_MAXPRICE', payload: e.target.value});
  }

  return (

      <div className="App">

        <input type="file" 
        name="XlFileLoader"
        id="XlFileLoader"
        accept="application/vnd.openxmlformats-officedocument.spreadsheetml.sheet" 
        onChange={(e)=>{
          // setLoaded(false);
          dispatch({type:'SET_LOADING', payload: false});
          const file = e.target.files[0];
          readFile(file);
        }}/>

        <Filters 
        minPrice={minPrice} 
        maxPrice={maxPrice}
        setMinPriceHandler={handleSetMinPrice}
        setMaxPriceHandler={handleSetMaxPrice}
        />

        { 
        dataReady &&
          !fail ? 
            ( !loaded ? 
            <DataTable data={data} minPrice={minPrice} maxPrice={maxPrice}/> : <h1>Still loading data...</h1>) : 
            (<h1>Failed to fetch data...</h1>) 
          }

      </div>
  );
}

export default App;
