import React, {useState, useEffect} from 'react';
import XLSX from 'xlsx';
import DataTable from './components/DataTable';
import Filters from './components/Filters';

function App() {

  const [data, setData] = useState([]);
  const [loaded, setLoaded] = useState(true);
  const [fail, setFail] = useState(false);
  const [minPrice, setMinPrice] = useState(0); 
  const [maxPrice, setMaxPrice] = useState(''); 

  /**
   * triggered when user uploads a file;
   * mainly for updating file if need be
   */ 
  const readFile = (file) => {
    const promise = new Promise((res, rej)=>{
      /* read file */
      const fileReader = new FileReader();
      fileReader.readAsArrayBuffer(file);

      fileReader.onload = (e) => {
        const arrayBuffer = e.target.result;
        /* now can read workbook */ 
        const workbook = XLSX.read(arrayBuffer, {type:'buffer'});
        const worksheetName = workbook.SheetNames[0];
        const workSheet = workbook.Sheets[worksheetName];
        const data = XLSX.utils.sheet_to_json(workSheet);
        res(data);
      }

      fileReader.onerror = (err) => rej(err);
    })

    promise.then((d)=>{
      setData(d);
      setLoaded(true);
    })
  }

  /* fetching data on mount */ 
  useEffect(()=>{
    /* fetch data from ./public/data.json */
    fetch('data.json', {
      headers:{
        "Content-Type": "application/json"
      }
    })
    .then(response=>{
      console.log('response:',response);
      return response.json();
    })
    .then(data=>{
      console.log('data:',data);
      setData(data);
      return setLoaded(false);
    })
    .catch(err=>{
      setLoaded(true);
      setFail(true);
      return console.log(err);
    })

  },[])

  /* update data on state change */ 
  useEffect(()=>{
    let newData = data[0].filter(obj=>{
      parseInt(obj.Price) > parseInt(minPrice)
    })
    setData(newData);

  }, [minPrice, maxPrice])

  const handleSetMinPrice = (e) => {
    return setMinPrice(e.target.value);
  }
  const handleSetMaxPrice = (e) => {
    return setMaxPrice(e.target.value);
  }

  return (
    <div className="App">

      <input type="file" 
      name="XlFileLoader"
      id="XlFileLoader"
      accept="application/vnd.openxmlformats-officedocument.spreadsheetml.sheet" 
      onChange={(e)=>{
        setLoaded(false);
        const file = e.target.files[0];
        readFile(file);
      }}/>

      <Filters 
      minPrice={minPrice} 
      maxPrice={maxPrice}
      setMinPriceHandler={handleSetMinPrice}
      setMaxPriceHandler={handleSetMaxPrice}
      />

      { 
      !fail ? 
        ( !loaded ? <DataTable data={data}/> : <h1>Still loading data...</h1>) : 
        (<h1>Failed to fetch data...</h1>) 
      }

    </div>
  );
}

export default App;
