import React, {useState, useEffect} from 'react';
import XLSX from 'xlsx';
import DataTable from './components/DataTable';

function App() {

  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  /**
   * triggered when user uploads a file;
   * mainly for updating file if need be
   */ 
  const readFile = (file) => {
    const promise = new Promise((res, rej)=>{
      /* read file */
      const fileReader = new FileReader();
      fileReader.readAsArrayBuffer(file);

      fileReader.onload = (e) => {
        const arrayBuffer = e.target.result;
        /* now can read workbook */ 
        const workbook = XLSX.read(arrayBuffer, {type:'buffer'});
        const worksheetName = workbook.SheetNames[0];
        const workSheet = workbook.Sheets[worksheetName];
        const data = XLSX.utils.sheet_to_json(workSheet);
        res(data);
      }

      fileReader.onerror = (err) => rej(err);
    })


    promise.then((d)=>{
      setData(d);
    })
  }

  useEffect(()=>{
    /* fetch data from ./public/data.json */
    fetch('data.json', {
      headers:{
        "Content-Type": "application/json"
      }
    })
    .then(response=>{
      console.log('response:',response);
      return response.json();
    })
    .then(data=>{
      console.log('data:',data);
      setLoading(false);
      return setData(data);
    })
    .catch(err=>{
      setLoading(false);
      return console.log(err);
    })

  },[])

  return (
    <div className="App">

      <input type="file" 
      name="XlFileLoader"
      id="XlFileLoader"
      accept="application/vnd.openxmlformats-officedocument.spreadsheetml.sheet" 
      onChange={(e)=>{
        const file = e.target.files[0];
        readFile(file);
      }}/>

      {!loading ? <DataTable data={data}/> : <h1>Still loading data...</h1> }


    </div>
  );
}

export default App;
